angular.module("portfolioApp").directive("projectInfo",function(){return{template:'<span md-highlight-text="searchText" style="padding-right:5px;">{{project.name}}</span><span md-highlight-text="searchText" style="font-size:.8em;" ng-repeat="tag in matchingTags track by $index">{{tag}}</span>',controller:["$scope","projectService","$timeout",function(a,b,c){a.$watch("searchText",function(d){c(function(){angular.isDefined(a.tags)&&(a.matchingTags=b.findMatchingTags(a.tags,d))})})}],scope:{searchText:"=searchText",tags:"=tags",project:"=project"}}});